import { Logger } from "winston";

export let logger: Logger;

export interface TracerConfig {
  clientId: string;
  brokers: string[];
  sasl: {
    mechanism: string;
    username: string;
    password: string;
  };
  ssl: {
    rejectUnauthorized: boolean;
    ca: string;
  };
  producer: {
    topic: string;
  };
}
export class OperationalTracer {
  constructor();
  init(config: TracerConfig): Promise<void>;
  error(service: string, message: Record<string, unknown>): void;
  info(service: string, message: Record<string, unknown>): void;
  warning(service: string, message: Record<string, unknown>): void;
  debug(service: string, message: Record<string, unknown>): void;
}

export const operationalTracer: OperationalTracer;
